  alert("clicked")
  // Get all input fields
  let fname = document.getElementsByClassName('fnameEntry').value
  let lname = document.getElementsByClassName('lnameEntry').value
  let password = document.getElementsByClassName('passwordEntry').value
  let email = document.getElementsByClassName('emailEntry').value
  if (ValidateField(email) == false || ValidateField(fname) || ValidateField(lname) || ValidatePassword(password) == false) {
    alert("Please fill out all fields correctly.")
    return
    // Wasn't value, stops continuing the code
  }
  auth.createUserWithEmailAndPassword(email, password)
  .then(function() {
    var user = auth.currentUser
    alert("User created")

    // Add user profile to Firebase Database
    var databaseRef = database.ref()

    // Create user data
    var userData = {
      email: email,
      fname: fname,
      lname: lname,
      lastLogin: Date.now()
    }

    databaseRef.child('users/' + user.uid).set(userData)
  })
  .catch(function(error) {
    var errorCode = error.errorcode
    var errorMessage = error.message
  })
}

