import React from 'react';
import './App.css';

import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import 'firebase/compat/firestore';
import "firebase/compat/database";
import {useAuthState} from 'react-firebase-hooks/auth';

firebase.initializeApp({
  apiKey: "AIzaSyC9UbICNpA767he7W-2PIbuyatG15nwni8",
  authDomain: "bitterr-e8569.firebaseapp.com",
  projectId: "bitterr-e8569",
  storageBucket: "bitterr-e8569.appspot.com",
  messagingSenderId: "120892097413",
  appId: "1:120892097413:web:720206f407c653c9f47aec",
  measurementId: "G-GSBPE4ME1Z"
})

const auth = firebase.auth();
const firestore = firebase.firestore();

function App() {

  return (
    <div className="App">
      <header className="App-header">

      </header>
      <body>
        <NavigationBar/>
        <div class = "bodyContent">
          <div class = "signInFrame">
              <h2>Sign In</h2>
              <div class = "fieldFrame">
                  <h4>Email</h4>
                  <input id = "email"/>
              </div>
              <div class = "fieldFrame">
                  <h4>Password</h4>
                  <input type = "password" id = "password"/>
                  <button class = "greenButton">Sign In</button>
              </div>
              <p>New to Bitterr? <a href = "roblox.com">Create an account</a></p>
          </div>
        </div>
        <Footer/>
      </body>
    </div>
  );
}

function NavigationBar() {
  return (
    <div class = "navigationBar">
        <img class = "navLogo" src = "images/appLogo.svg"/>
        <h3>Bitterr</h3>
    </div>
  )
}

function Footer() {
  return (
    <div class = "footer">
          <h2>TigerHacks 2023 Competition</h2>
          <p>Created by Derek Nissen, Ashton Wooster, Tanner Kuchar, & Carter Cox</p>
    </div>
  )
}

export default App;
