/* REACT DEPENDENCIES */
import React from 'react'

/* FIREBASE DEPENDENCIES */
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import 'firebase/compat/firestore';
import "firebase/compat/database";
import {useAuthState} from 'react-firebase-hooks/auth';

firebase.initializeApp({
  apiKey: "AIzaSyC9UbICNpA767he7W-2PIbuyatG15nwni8",
  authDomain: "bitterr-e8569.firebaseapp.com",
  projectId: "bitterr-e8569",
  storageBucket: "bitterr-e8569.appspot.com",
  messagingSenderId: "120892097413",
  appId: "1:120892097413:web:720206f407c653c9f47aec",
  measurementId: "G-GSBPE4ME1Z"
});

const database = firebase.database()
const auth = firebase.auth()

/* FIREBASE FUNCTIONS */

export function Register() {
    // Get all input fields
    let fname = document.getElementsByClassName('fnameEntry').value
    let lname = document.getElementsByClassName('lnameEntry').value
    let password = document.getElementsByClassName('passwordEntry').value
    let email = document.getElementsByClassName('emailEntry').value
    if (ValidateField(email) == false || ValidateField(fname) || ValidateField(lname) || ValidatePassword(password) == false) {
      alert("Please fill out all fields correctly.")
      return
      // Wasn't value, stops continuing the code
    }
    auth.createUserWithEmailAndPassword(email, password)
    .then(function() {
      var user = auth.currentUser
      alert("User created")

      // Add user profile to Firebase Database
      var databaseRef = database.ref()

      // Create user data
      var userData = {
        email: email,
        fname: fname,
        lname: lname,
        lastLogin: Date.now()
      }

      databaseRef.child('users/' + user.uid).set(userData)
    })
    .catch(function(error) {
      var errorCode = error.errorcode
      var errorMessage = error.message
    })
  }

function ValidatePassword(password) {
    if (password < 6) {
        return false
    } else {
        return true
    }
}

function ValidateField(field) {
    if (field == null) {
    return false
    }

    if (field.length <= 0) {
    return false
    } else {
    return true
    }
}
